import React, {useEffect, useRef} from "react";
import './App.css';
import axios from 'axios';

import { BrowserRouter as Router, 
  Route, 
  Routes,  
  Navigate,
  Outlet
} from "react-router-dom";


//Pages
import HomePage from "./Pages/HomePage";
import Login from "./Pages/LoginPage";
import Register from "./Pages/RegisterPage";
import DashboardPage from "./Pages/DashboardPage.js";

const ProtectedRoute = ({children, redirectPath = '/'}) => {
  const mountedRef = useRef(true);
  const [user, setUser] = React.useState(null);
  const [loading, setLoading] = useState(false);
  useEffect( () => {
    //get user info
    if(!user) {
      console.log("setting user");
      axios.get(`${url}/auth`).then(res=> {
        console.log(res.data);
        if(res.data) {
          console.log(res.data);
          setUser(res.data);
          setLoading(true);
        }
      })
      console.log("set user");
      return () => {
        mountedRef.current = false;
      }
    }

  },[]);
//problem, below runs first so run effect never has a chance to do anything
  // if(!user) {
  //   console.log("not auth");
  //   return <Navigate to={redirectPath} replace/>;
  // }
  // return children ? children : <Outlet user={user}/>
return (
  <div>
    {
      user ? <div> children ? children : <Outlet user={user}/> </div> : <Navigate to={redirectPath} replace/>

    }
  </div>
  )
}

const url = "http://localhost:5000";

function App() {

  return (
    <div> 
      <Router> 
          <Routes> 
            <Route exact path="/" element={<HomePage/>} />
            <Route exact path="/login" element={<Login/>} />
            <Route exact path="/register" element={<Register/>} />
            {/* Add more routes to this if you want to have more user authenticated pages that redirect to home (same level protection)*/}
            {/* <Route element={<ProtectedRoute user={user} />}> 
              <Route exact path="/dashboard" element={<DashboardPage />} />
            </Route> */}
            {/* Protect a single route to redirect */}
            <Route exact path = "/dashboard" 
                  element={
                  <ProtectedRoute>
                    <DashboardPage/>
                  </ProtectedRoute>} />
            <Route 
              path="*"
              element={<Navigate to="/" replace />}
            />
          </Routes>
      </Router>

    </div>
  );
}

export default App;
