import React from "react";
import './StopWatch.css'

const StopWatch = (props) => {
  const [time, setTime] = React.useState(0);
  const [timerOn, setTimerOn] = React.useState(false);
  const [taskName, setTaskName] = React.useState("");
  const [taskDesc, setTaskDesc] = React.useState("");



  React.useEffect(() => {
    let interval = null;

    if (timerOn) {
      interval = setInterval(() => {
        setTime((prevTime) => prevTime + 10);
      }, 10);
    } else if (!timerOn) {
      clearInterval(interval);
    }

    return () => clearInterval(interval);
  }, [timerOn]);

  const addTask = (e) => {
    var today = new Date();
    var dd = String(today.getDate()).padStart(2, '0');
    var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
    var yyyy = today.getFullYear();
    today = mm + '/' + dd + '/' + yyyy;

    const task = {
      name: taskName,
      description: taskDesc,
      elapsedTime: time,
      date: today
    }
    axios.post(`${url}/add`, task, {withCredentials: true}).then(
      res => {
        if(res.status >= 200 && res.status <= 299) {
          console.log(res.data);
          console.log("posted");
          handleClose();
          window.location.reload(false);
        }
      }
    ).catch((error) => {
      // setError("error logging out");
      console.log(error);
    });
  }

  return (
    <div className="Timers">
      <h2>Stopwatch</h2>
      <div id="display">
        <span>{("0" + Math.floor((time / 60000) % 60)).slice(-2)}:</span>
        <span>{("0" + Math.floor((time / 1000) % 60)).slice(-2)}:</span>
        <span>{("0" + ((time / 10) % 100)).slice(-2)}</span>
      </div>

      <div id="buttons">
        {!timerOn && time === 0 && (
          <button style={{backgroundColor: "rgb(51, 153, 255)"}} onClick={() => setTimerOn(true)}>Start</button>
        )}
        {timerOn && <button onClick={() => setTimerOn(false)}>Stop</button>}

        {!timerOn && time > 0 && (
          <button style={{backgroundColor: "rgb(51, 153, 255)"}} onClick={() => setTimerOn(true)}>Resume</button>
        )}
        {!timerOn && time > 0 && (
          <button onClick={() => setTime(0)}>Reset</button>
        )}
        {!timerOn && time > 0 && (
          <button style={{backgroundColor: "rgb(102, 255, 178)", marginTop: "10px"}} onClick={() => setTime(0)}>Submit</button>
        )}

      </div>
    </div>
  );
};

export default StopWatch;
