import AppBar from "./AppBar.js";  
import React, {useEffect, useRef} from "react";
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import AddIcon from '@mui/icons-material/Add';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import DeleteIcon from '@mui/icons-material/Delete';
import Table from './Table';

const style = {
  width: '100%',
  maxWidth: 360,
  bgcolor: 'background.paper',
};


const url = "http://localhost:5000";


function ResponsiveDrawer(props) {

    const mountedRef = useRef(true);
    const [user, setUser] = React.useState({});
    const [loading, setLoading] = React.useState(true);
    const [list, setList] = React.useState([]);
    const navigate = useNavigate();
    useEffect( () => {
      //get user info
      // console.log(loading);
      axios.get(`${url}/auth`, {withCredentials: true}).then(res=> {
        if(res.data) {
          setUser(res.data);
          setList(res.data.completedTasks);
          // console.log("loading is false");
          console.log("set user to " + JSON.stringify(res.data));

          setLoading(false);
        }
        else{
            navigate("/");
        }
      }).catch((err) => {
        // not needed
        console.log(err);
        setLoading(false);
      })
      return () => {
        mountedRef.current = false;
      }

    },[]);

  const revealOverlay = (e) => {
    // console.log(list);
    // console.log(list.length);
    // for(let i = 0; i < list.length; i++) {
    //   console.log(list[i]);
    // }
    const task = {
      name: "example task",
      description: "example desc",
      elapsedTime: "hh:mm:ss",
      date: "yy:mm:dd"
    }
    axios.post(`${url}/add`, task, {withCredentials: true}).then(
      res => {
        if(res.status >= 200 && res.status <= 299) {
          console.log(res.data);
          console.log("posted");
          window.location.reload(false);
        }
      }
    ).catch((error) => {
      // setError("error logging out");
      console.log(error);
    });
  }

  const handleSubmit = e => { 

  }    

  if(!loading) {
    return (
      <div>
          <AppBar isLoggedIn={true}/> 
          <div style={{display: "flex", justifyContent: "center", alignItems: "center"}}>
            <div style={{display: "flex", justifyContent: "center", alignItems: "center", height: "90px", width:"90%", borderRadius: "10px", marginTop:"20px", boxShadow: "0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)"}}>
              <Button onClick={revealOverlay}>           
                <AddIcon/>
              </Button>
            </div>
          </div>
          <div style={{display: "flex", justifyContent: "center", alignItems: "center"}}>
            <div style={{display: "flex", flexDirection: "column", justifyContent: "center", alignItems: "center", width:"90%", marginTop:"20px", boxShadow: "0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)"}}>
              <Typography component="h1" variant="h5"sx={{margin:"20px 0px"}}>
                Completed Tasks
              </Typography>
              <Divider/>
              <Table list={list}/>

              {/* <List component="nav" aria-label="mailbox folders">
                {list.map((task) => {
                  const name = `Name: ${task.name}`;
                  const description = `Description: ${task.description}`;
                  const elapsedTime = `Elapsed time: ${task.elapsedTime}`;
                  const date = `Date completed: ${task.date}`;

                  return (
                    <ListItem>

                      <ListItemText primary={name} sx={{padding: "80px 40px", float: 'left', width: '25%'}}/> 

                      <ListItemText primary={description}/>
                      <ListItemText primary={elapsedTime}/>
                      <ListItemText primary={date}/>
                      <DeleteIcon />

                    </ListItem>

                  )
                })}

              </List> */}
            </div>
          </div>
      </div>
    )   
    return (
      <h1> </h1>
    )
  }

}

  export default ResponsiveDrawer;
