import React, {useEffect} from "react";
import './App.css';
import axios from 'axios';

import { BrowserRouter as Router, 
  Route, 
  Routes,  
  Navigate,
  Outlet
} from "react-router-dom";


//Pages
import HomePage from "./Pages/HomePage";
import Login from "./Pages/LoginPage";
import Register from "./Pages/RegisterPage";
import DashboardPage from "./Pages/DashboardPage.js";

const ProtectedRoute = ({user, children, redirectPath = '/'}) => {
  if(!user) {
    return <Navigate to={redirectPath} replace/>;
  }
  return children ? children : <Outlet/>
}

const url = "http://localhost:5000";

function App() {

  const [user, setUser] = React.useState(null);

  useEffect( () => {
    //get user info
    if(!user) {
      axios.get(`${url}/auth/`).then(res=> {
        if(res.data) {
          setUser(res.data);
        }
      })
    }

  });

  return (
    <div> 
      <Router> 
          <Routes> 
            <Route exact path="/" element={<HomePage/>} />
            <Route exact path="/login" element={<Login/>} />
            <Route exact path="/register" element={<Register/>} />
            {/* Add more routes to this if you want to have more user authenticated pages that redirect to home (same level protection)*/}
            {/* <Route element={<ProtectedRoute user={user} />}> 
              <Route exact path="/dashboard" element={<DashboardPage />} />
            </Route> */}
            {/* Protect a single route to redirect */}
            <Route exact path = "/dashboard" 
                  element={
                  <ProtectedRoute user={user}>
                    <DashboardPage/>
                  </ProtectedRoute>} />
            <Route 
              path="*"
              element={<Navigate to="/" replace />}
            />
          </Routes>
      </Router>

    </div>
  );
}

export default App;
