import React, {useEffect, useRef} from "react";
import './App.css';
import axios from 'axios';
import { CircularProgress, formControlClasses } from "@mui/material";

import { BrowserRouter as Router, 
  Route, 
  Routes,  
  Navigate,
  Outlet
} from "react-router-dom";


//Pages
import HomePage from "./Pages/HomePage";
import Login from "./Pages/LoginPage";
import Register from "./Pages/RegisterPage";
import DashboardPage from "./Pages/DashboardPage.js";

const ProtectedRoute = ({user, loading, children, redirectPath = '/'}) => {
  // const mountedRef = useRef(true);
  // const [user, setUser] = React.useState({});
  // const [loading, setLoading] = React.useState(true);
  // useEffect( () => {
  //   //get user info
  //   console.log(loading);
  //   axios.get(`${url}/auth`, {withCredentials: true}).then(res=> {
  //     if(res.data) {
  //       console.log(res.data);
  //       setUser(res.data);
  //       console.log("loading is true");
  //       console.log("set user to " + JSON.stringify(res.data));
  //     }
  //     setLoading(false);
  //   }).catch((err) => {
  //     // not needed
  //     console.log(err);
  //     setLoading(false);
  //   })
  //   return () => {
  //     mountedRef.current = false;
  //   }

  // },[]);

  if(loading && user === null) {
    return(
      <div style={{display:"flex", justifyContent:"center"}}> <CircularProgress/> </div>
    )
  }

  //redirect if we are not loading anymore and empty object returned (logged)
  if(Object.keys(user).length === 0 && !loading) {
    console.log("not auth, redirecting");
    return <Navigate to={redirectPath} replace/>;
  }
  else{
    return children;
  }

}

const url = "http://localhost:5000";

function App() {

  const mountedRef = useRef(true);
  const [user, setUser] = React.useState({});
  const [loading, setLoading] = React.useState(true);
  useEffect( () => {
    //get user info
    console.log(loading);
    axios.get(`${url}/auth`, {withCredentials: true}).then(res=> {
      if(res.data) {
        console.log(res.data);
        setUser(res.data);
        console.log("loading is false");
        console.log("set user to " + JSON.stringify(res.data));
        setLoading(false);
      }
    }).catch((err) => {
      // not needed
      console.log(err);
      setLoading(false);
    })
    return () => {
      mountedRef.current = false;
    }

  },[]);

  return (
    <div> 
      <Router> 
          <Routes> 
            <Route exact path="/" element={<HomePage/>} />
            <Route exact path="/login" element={<Login/>} />
            <Route exact path="/register" element={<Register/>} />
            {/* Add more routes to this if you want to have more user authenticated pages that redirect to home (same level protection)*/}
            {/* <Route element={<ProtectedRoute user={user} />}> 
              <Route exact path="/dashboard" element={<DashboardPage />} />
            </Route> */}
            {/* Protect a single route to redirect */}
            <Route exact path = "/dashboard" 
                  element={
                  <ProtectedRoute user={user} loading={loading}>
                    <DashboardPage/>
                  </ProtectedRoute>} />
            <Route 
              path="*"
              element={<Navigate to="/" replace />}
            />
          </Routes>
      </Router>

    </div>
  );
}

export default App;
