import * as React from 'react';
import Backdrop from '@mui/material/Backdrop';
import Button from '@mui/material/Button';
import AddIcon from '@mui/icons-material/Add';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import CloseIcon from '@mui/icons-material/Close';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';



import axios from 'axios';


const url = "http://localhost:5000";


export default function SimpleBackdrop() {
  const [open, setOpen] = React.useState(false);
  const [taskState, setTaskState] = React.useState(false); //false means not started, true means started

  const handleClose = () => {
    setOpen(false);
  };
  const handleToggle = () => {
    setOpen(!open);
  };

  const startTrack = (e) => {
    setTaskState(true);
  }

  const stopTrack = (e) => {
    setTaskState(false);
  }


  const addTask = (e) => {
    const task = {
      name: "example task",
      description: "example desc",
      elapsedTime: "hh:mm:ss",
      date: "yy:mm:dd"
    }
    axios.post(`${url}/add`, task, {withCredentials: true}).then(
      res => {
        if(res.status >= 200 && res.status <= 299) {
          console.log(res.data);
          console.log("posted");
          window.location.reload(false);
        }
      }
    ).catch((error) => {
      // setError("error logging out");
      console.log(error);
    });
  }

  return (
    <div>
      <Button onClick={handleToggle}><AddIcon/></Button>
      <Backdrop
        sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
        open={open}
      >
        <div  style={{width: "50%", height: "90%", backgroundColor: "white"}}>
            <div style={{display: "flex", justifyContent:"flex-end"}}>
                <Button onClick={handleClose} sx={{color: "black"}}> <CloseIcon/> </Button>
            </div>
            <div style={{display: "flex", alignItems: "center", flexDirection:"column"}}> 

            </div>

            <Typography component="h1" variant="h5" sx={{color: "black"}}>
                Create task
            </Typography>
            <Box> 00:00:00 </Box>
            <Box component="form" noValidate sx={{ mt: 1 }}>

                <TextField
                margin="normal"
                required
                fullWidth
                id="name"
                label="Task name"
                name="name"
                autoComplete="name"
                autoFocus
                />
                <TextField
                margin="normal"
                required
                fullWidth
                name="desciption"
                label="Desciption"
                type="desciption"
                id="desciption"
                autoComplete="task-desciption"
                />
                {taskState ?  
                    <div style={{display: "flex", justifyContent:"flex-end"}}>
                        <Button
                            variant="contained"
                            sx={{ mt: 3, mb: 2 }}
                            onClick={stopTrack}
                            >
                            Stop
                        </Button> 
                    </div>
                    : 
                    <div style={{display: "flex", justifyContent:"flex-end"}}>
                        <Button
                            variant="contained"
                            sx={{ mt: 3, mb: 2 }}
                            onClick={startTrack}
                            >
                            Start
                        </Button>
                    </div>

                }

                <Grid container>
                <Grid item xs>
                    {/* <Link href="#" variant="body2">
                    Forgot password?
                    </Link> */}
                </Grid>
                <Grid item>
                    {/* <Link href="/register" variant="body2">
                    {"Don't have an account? Sign Up"}
                    </Link> */}
                </Grid>
                </Grid>
            </Box>
        </div>
      </Backdrop>
    </div>
  );
}
